$(function(){
		$('.cancel').click(function(){
			$('.addrt').hide()
		})
		$('.add').click(function(){
			$('.addrt').show()
			$('.sendrt').hide()
			$('.distrt').hide()
			$('.modify').hide()
		})
		$('.sendcancel').click(function(){
			$('.sendrt').hide()
		})
		$('.send').click(function(){
			$('.sendrt').show()
			$('.addrt').hide()
			$('.distrt').hide()
			$('.modify').hide()
		})
		$('.distribute').click(function(){
			$('.distrt').show()
			$('.addrt').hide()
			$('.sendrt').hide()
			$('.modify').hide()
		})
		$('.discancel').click(function(){
			$('.distrt').hide()
		})
		$('.modifypwd').click(function(){
			$('.modify').show()
			$('.sendrt').hide()
			$('.addrt').hide()
			$('.distrt').hide()
		})
		$('.modcancel').click(function(){
			$('.modify').hide()
		})
		$('.forgetpwd').click(function(){
			$('.findpassword').show()
		})
		$('.hidemail').click(function(){
			$('.findpassword').hide()
		})
		$('.delete').each(function(index){
			$(this).click(function(){
				$(this).parent('ul').parent('.admincont').hide()
			})
		})
		$('.admincont ul li:nth-of-type(6)').each(function(index){
			$(this).hover(function(){
				$(this).css('white-space','normal')
			})
			$(this).mouseout(function(){
				$(this).css('white-space','nowrap')
			})
		})
	})